mapper.select:
select * from Item_info

	and code = #{code}
	and name = #{name}
	and price = #{price}
	and orgin = #{orgin}
	and info = #{info}
	and image = #{image}

mapper.delete:
delete from item_info

	and code = #{code}
	and name = #{name}
	and price = #{price}
	and orgin = #{orgin}
	and info = #{info}
	and image = #{image}

mapper.update:
update item_info set name = #{name},
price = #{price},
orgin = #{orgin},
info = #{info},
image = #{image}
where code = #{code}
